    private void RadioBMulherActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_RadioBMulherActionPerformed
        if (evt.getSource() == RadioBMulher) {
            RadioBHomem.setSelected(false);
        }
    }//GEN-LAST:event_RadioBMulherActionPerformed

    private void RadioBHomemActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_RadioBHomemActionPerformed
        if (evt.getSource() == RadioBHomem) {
            RadioBMulher.setSelected(false);
        }
    }//GEN-LAST:event_RadioBHomemActionPerformed

    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        Double Altura, Peso, Idade, resultadoGB = 0.0, resultadoGT, calculoGT;
        String alturaStr = TxAltura.getText(), pesoStr = TxPeso.getText();
        String idadeStr = TxIdade.getText();
        
        if (!RadioBMulher.isSelected() && !RadioBHomem.isSelected() && !alturaStr.isEmpty() && !pesoStr.isEmpty() && !idadeStr.isEmpty()) {
            try {
                
                Altura = Double.parseDouble(alturaStr);
                Peso = Double.parseDouble(pesoStr);
                Idade = Double.parseDouble(idadeStr);
                
                if (RadioBMulher.isSelected()){
                    resultadoGB = 66.0 + (13.8 * Peso) + (5.0 * Altura) - (6.8 * Idade);
                } else if (RadioBHomem.isSelected()){
                    resultadoGB = 655.0 + (9.6 * Peso) + (1.9 * Altura) - (4.7 * Idade);
                }

                String GBstring = String.valueOf(resultadoGB);
                GastoBasalResp.setText(GBstring);
                
                calculoGT = switch (NivelAtividade.getSelectedIndex()) {
                    case 0 -> 1.2;
                    case 1 -> 1.375;
                    case 2 -> 1.55;
                    case 3 -> 1.725;
                    case 4 -> 1.9;
                    default -> 0.0;
                };
                
                if (calculoGT == 0.0) {
                    GastoTotalResp.setText("Informe seu nível de atividade.");
                } else {
                    resultadoGT = resultadoGB * calculoGT;
                    String GTstring = String.valueOf(resultadoGT);
                    GastoTotalResp.setText(GTstring);
                }
            } catch (NumberFormatException e) {
                GastoBasalResp.setText("Valores inválidos, tente novamente.");
            }
        } else {
            GastoBasalResp.setText("Preencha todos os campos e informe seu sexo.");
        }
                
    }//GEN-LAST:event_jButton2ActionPerformed

